import random

from web3 import Web3, HTTPProvider
import sqlite3


def dict_factory(cursor, row):
    d = {}
    for idx, col in enumerate(cursor.description):
        d[col[0]] = row[idx]
    return d


rpc_jh = [
    'https://arbitrum-one.public.blastapi.io', ]

rpc = random.choice(rpc_jh)
web3 = Web3(HTTPProvider(rpc))
print(rpc)


