    con = sqlite3.connect("F:/sqlite/撸毛库.db")
    con.row_factory = dict_factory
    cur = con.cursor()
    addre = cur.execute("SELECT * FROM  Account_info  GROUP BY id LIMIT 27,1").fetchall()

    for i in addre:
        f_seq = i["zw_name"]
        f_address = i["address"]
        f_target_address = i["ex_address"]
        f_private_key = i["phrase_key"]
        f_amount = 625 * 10 ** 18
        arb_transfer(f_seq, f_address, f_target_address, f_amount, f_private_key)
